import React from 'react'
import ImageHelper from "./helper/imageHelper"
import { Redirect } from "react-router-dom"

isAuthenticated = true

const Card = ({ product, addtoCart = true, removeFromCart = false }) => {
    const cartTitle = product ? product.name : "Sample image"
    const cartDescription = product ? product.description : "Sample description"
    const cartPrice = product ? product.price : "100"

    const addToCart = () => {
        if (isAuthenticated) {
            console.log("Added to cart")
        }
        else {
            console.log("Login please")
        }
    }

    const getAredirect = redirect => {
        if (redirect) {
            return <Redirect to="/cart" />
        }
    }

    const showAddToCart = addToCart => {
        return (
            addToCart && (
                <button
                    onClick={() => { addToCart }}
                    className="btn btn-block btn-outline-success mt-2 mb-2"
                >
                    Add to Cart
                </button>
            )
        )
    }

    return (
        <div className="card text-white bg-dark border border-info ">
            <div className="card-header lead">{cartTitle}</div>
            <div className="card-body">
                <ImageHelper product={product} />
                <p className="lead bg-success font-weight-normal text-wrap">
                    {cartDescription}
                </p>
                <p className="btn btn-success rounded  btn-sm px-4">Rs {cartPrice}</p>
                <div className="row">
                    <div className="col-12">
                        {showAddToCart(addToCart)}
                    </div>
                    <div className="col-12">
                        <button
                            onClick={() => { }}
                            className="btn btn-block btn-outline-danger mt-2 mb-2"
                        >
                            Remove from cart
              </button>
                    </div>
                </div>
            </div>
        </div>
    );
};
export default Card;
